import Header from "./component/page/Header";
import Dashboard from "./component/page/DashBoard";
import React, { useState } from "react";
import LoginSignup from "./component/page/LoginSignUp";
import {
  BrowserRouter as Router,
  Route,
  Navigate,
  Routes,
} from "react-router-dom";

import routes from "./component/router";

const AuthenticatedContent: React.FC = () => {
  return (
    <div>
      <p>Welcome! You are logged in.</p>
      {/* Render authenticated content here */}
    </div>
  );
};

function App() {
  const [token, setToken] = useState<string | null>(null);

  const handleLoginSuccess = (newToken: string) => {
    setToken(newToken);
    // You may want to redirect the user or perform other actions on successful login
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={<LoginSignup onLoginSuccess={handleLoginSuccess} />}
        />
        {/* {token ? (
                <Navigate to="/authenticated" />
              ) : (
                <LoginSignup onLoginSuccess={handleLoginSuccess} />
              )}
            </Route>
            <Route path="/authenticated">
              {token ? <AuthenticatedContent /> : <Navigate to="/login" />} */}
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/Header" element={<Header text="Welcome to My App" />} />
      </Routes>
    </Router>
  );
}

export default App;
